import './style.scss';

import { useState } from 'react';

import { Container } from '../Layout/';
import { Navigator } from '../Navigator/';
import { Link } from '../Link/';
import { Logo } from '../Logo/';
import { List, ListItem } from '../List/';

export function Header(props)
{
    const mainClass = 'header';
    let secondaryClass = props.className;

    if (secondaryClass === undefined)
    {
        secondaryClass = '';
    }

    return(
        <header className={mainClass + " " + secondaryClass}>
            {props.children}
        </header>
    )
}

function MenuButton(props)
{
    let displayMenuState = props.displayState;
    let setMenuState = props.setState;

    return(
        <button
            className='mobile'
            id='menu_button'

            onClick={() => {
                if (displayMenuState === 'none')
                {
                    setMenuState('flex !important');
                } else {
                    setMenuState('none')
                }

                console.log(displayMenuState);
            }}
        >
            <div className='menu_hamburguer'>
                <div id='1_barra' className='barra'></div>
                <div id='2_barra' className='barra'></div>
                <div id='3_barra' className='barra'></div>
            </div>
        </button>
    )
}

export function NavMenu()
{
    let [displayMenuState, setMenuState] = useState('none');

    return(
        <Header className="nav-menu">
            <Container>
                <Navigator>
                    <MenuButton displayState={displayMenuState} setState={setMenuState} />

                    <Link id='logo_topo'>
                        <Logo />
                    </Link>

                    <List className='desktop'>
                        <ListItem>
                            <Link>Soluções</Link>
                        </ListItem>

                        <ListItem>
                            <Link>Sobre nós</Link>
                        </ListItem>

                        <ListItem>
                            <Link>Contato</Link>
                        </ListItem>

                        <ListItem>
                            <Link>Vagas</Link>
                        </ListItem>
                    </List>

                    <div
                        id='menu_mobile'

                        style={{
                            display: displayMenuState
                        }}
                    >
                        <List>
                            <ListItem>
                                <Link>Home</Link>
                            </ListItem>

                            <ListItem>
                                <Link>Sobre nós</Link>
                            </ListItem>

                            <ListItem>
                                <Link>Contato</Link>
                            </ListItem>

                            <ListItem>
                                <Link>Vagas</Link>
                            </ListItem>
                        </List>
                    </div>
                </Navigator>
            </Container>
        </Header>
    )
}
