import './style.scss';

import { useState } from 'react';

import { Container } from '../Layout/';
import { Navigator } from '../Navigator/';
import { Link } from '../Link/';
import { Logo } from '../Logo/';
import { List, ListItem } from '../List/';

export function Header(props)
{
    const mainClass = 'header';
    let secondaryClass = props.className;

    if (secondaryClass === undefined)
    {
        secondaryClass = '';
    }

    return(
        <header className={mainClass + " " + secondaryClass}>
            {props.children}
        </header>
    )
}

export function NavMenu()
{
    const [displayMenuState, setMenuState] = useState('none');
    console.log(displayMenuState)

    return(
        <Header className="nav-menu">
            <Container>
                <Navigator>
                    <button
                        className='mobile'
                        id='menu_button'
                        style={{backgroundColor: displayMenuState === 'flex !important' ? 'red' : 'blue'}}
                        onClick={() => {
                            if (displayMenuState === 'none')
                            {
                                setMenuState('flex !important');
                            } else {
                                setMenuState('none')
                            }


                        }}
                    >
                        <div className='menu_hamburguer'>
                            <div id='1_barra' className='barra'></div>
                            <div id='2_barra' className='barra'></div>
                            <div id='3_barra' className='barra'></div>
                        </div>
                    </button>

                    <Link id='logo_topo'>
                        <Logo />
                    </Link>

                    <List className='desktop'>
                        <ListItem>
                            <Link>Soluções</Link>
                        </ListItem>

                        <ListItem>
                            <Link>Sobre nós</Link>
                        </ListItem>

                        <ListItem>
                            <Link>Contato</Link>
                        </ListItem>

                        <ListItem>
                            <Link>Vagas</Link>
                        </ListItem>
                    </List>

                    <div
                        id='menu_mobile'

                        style={{
                            display: displayMenuState
                        }}
                    >
                        <List>
                            <ListItem>
                                <Link>Home</Link>
                            </ListItem>

                            <ListItem>
                                <Link>Sobre nós</Link>
                            </ListItem>

                            <ListItem>
                                <Link>Contato</Link>
                            </ListItem>

                            <ListItem>
                                <Link>Vagas</Link>
                            </ListItem>
                        </List>
                    </div>
                </Navigator>
            </Container>
        </Header>
    )
}
