import './style.scss';

import reactDom from 'react-dom';
import { useState } from 'react';

import { Container } from '../Layout/';
import { Navigator } from '../Navigator/';
import { Link } from '../Link/';
import { Logo } from '../Logo/';
import { List, ListItem } from '../List/';

export function Header(props)
{
    const mainClass = 'header';
    let secondaryClass = props.className;

    if (secondaryClass === undefined)
    {
        secondaryClass = '';
    }

    return(
        <header className={mainClass + " " + secondaryClass}>
            {props.children}
        </header>
    )
}

export function menuResponsivo()
{
    let containerMenu = document.getElementById('menu_responsivo');

    return(
        reactDom.createPortal(
            <div
                id='menu_mobile'

                style={{
                    display: menuVisibility ? 'flex' : 'none'
                }}
            >
                <List>
                    <ListItem>
                        <Link>Home</Link>
                    </ListItem>

                    <ListItem>
                        <Link>Sobre nós</Link>
                    </ListItem>

                    <ListItem>
                        <Link>Contato</Link>
                    </ListItem>

                    <ListItem>
                        <Link>Vagas</Link>
                    </ListItem>
                </List>
            </div>,
            containerMenu
        )
    )
}

